mapper.select:
select 

from t_career_item
where career_id = #{careerId,jdbcType=INTEGER} 

and enabled = #{enabled,jdbcType=INTEGER}

mapper.insert:
insert into t_career_item (id, person_id, career_id, 
  start_date, end_date, is_current, 
  department, position, enabled, 
  create_time, last_time, description
  )
values (#{id,jdbcType=INTEGER}, #{personId,jdbcType=INTEGER}, #{careerId,jdbcType=INTEGER}, 
  #{startDate,jdbcType=DATE}, #{endDate,jdbcType=DATE}, #{isCurrent,jdbcType=TINYINT}, 
  #{department,jdbcType=VARCHAR}, #{position,jdbcType=VARCHAR}, #{enabled,jdbcType=TINYINT}, 
  #{createTime,jdbcType=TIMESTAMP}, #{lastTime,jdbcType=TIMESTAMP}, #{description,jdbcType=LONGVARCHAR}
  )
